package ControlStatement;

public class IncrementControl {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int control = 10;
		
		while (control <= 90 ) {
			
		System.out.println("The control statement will keep running till it gets to" + control);
			
		control = control +  5;
		}
	}

}
