package DataTypes;

public class PrimitiveData {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int MyText =  50;
		System.out.println("my confidence level has gone to 50/" + MyText);
		
		
		short MyText2 = 100;
		System.out.println("My confidence on short as well is 100/" + MyText2);
		
		boolean MyText3 =  true;
		System.out.println("my confidence level has gone through the roof " + MyText3);
		
		
		char MyText4 = 'T';
		System.out.println("My java competency is now to the " + MyText4);
		
	}

}
